mapper.update:
update tbooking
	set book_Status='RS_STT_BC'
	where book_num=#{bookNum}

update tbooking set user_num = #{userNum}, rom_num =#{romNum}, book_Date=#{bookDate}, checkin_Date=#{checkinDate}, checkout_Date=#{checkoutDate}, staydays=#{staydays}, guest=#{guest}, book_Price=#{bookPrice}, expected_Arr=#{expectedArr}, smoking=#{smoking}, request=#{request}, real_Arr=#{realArr},
room_Name=#{roomName}, U_LASTNAME=#{lastName}, U_FSTNAME=#{firstName}, book_Status=#{bookStatus}
where book_num=#{bookNum}
