mapper.select:
SELECT rev.PST_NUM, rev.BRD_CODE, rev.p_title, rev.p_regdate, rev.p_updatedate, rev.user_num, rev.rev_purl, rev.STISF, rev.acm_num, rev.book_num, rev.content, rev.acm_name, bk.CHECKIN_DATE, bk.CHEKOUT_DATE from (SELECT r.PST_NUM, r.BRD_CODE, r.p_title, r.p_regdate, r.p_updatedate, r.user_num, r.rev_purl, r.STISF, r.acm_num, r.book_num, r.content, t.acm_name FROM (select A.PST_NUM, A.BRD_CODE, a.p_title, a.p_regdate, a.p_updatedate, a.user_num, b.rev_purl, B.STISF, b.acm_num, b.book_num, b.content FROM TREV_POST A LEFT JOIN trev_detail B ON a.pst_num = b.pst_num) R , TACM T where r.acm_num = t.acm_num ) rev,
tbooking bk
where rev.book_num = bk.book_num AND rev.user_num = #{loginUser}

select A.PST_NUM, A.BRD_CODE, a.p_title, a.p_regdate, a.p_updatedate, a.user_num, b.rev_purl, B.STISF, b.acm_num, b.book_num, b.content
FROM TREV_POST A LEFT JOIN trev_detail B ON a.pst_num = b.pst_num
where a.PST_NUM = #{pstNum}
